import React, { useState, useCallback } from "react";
import ReactFlow, {
  addEdge,
  useNodesState,
  useEdgesState,
  MiniMap,
  Controls,
  Background,
} from "reactflow";
import axios from "axios";
import "reactflow/dist/style.css";

const initialNodes = [
  {
    id: "1",
    type: "input",
    data: { label: "Start" },
    position: { x: 250, y: 5 },
  },
];

const initialEdges = [];

const FlowChart = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  const [selectedNode, setSelectedNode] = useState(null);
  const [nodeDetails, setNodeDetails] = useState({
    subject: "",
    body: "",
    time: "",
    days: "",
    emails: "",
  });

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    [setEdges]
  );

  const addNode = (type) => {
    const id = (nodes.length + 1).toString();
    const newNode = {
      id,
      data: { label: type },
      position: { x: Math.random() * 400, y: Math.random() * 400 },
    };
    setNodes((nds) => [...nds, newNode]);

    if (nodes.length > 0) {
      setEdges((eds) => [
        ...eds,
        { id: `e${nodes[nodes.length - 1].id}-${id}`, source: nodes[nodes.length - 1].id, target: id },
      ]);
    }
  };

  const handleNodeClick = (event, node) => {
    if (
      node.data.label === "Cold Email" ||
      node.data.label === "Wait" ||
      node.data.label === "Lead Source"
    ) {
      setSelectedNode(node);
    }
  };

  const handleDetailChange = (e) => {
    const { name, value } = e.target;
    setNodeDetails((prev) => ({ ...prev, [name]: value }));
  };

  const saveNodeDetails = async () => {
    const updatedNodes = nodes.map((node) =>
      node.id === selectedNode.id
        ? {
            ...node,
            data: {
              ...node.data,
              details: { ...nodeDetails },
              label:
                selectedNode.data.label === "Cold Email" ? (
                  <div>
                    <strong>Cold Email</strong>
                    <br />
                    <small>Subject: {nodeDetails.subject}</small>
                  </div>
                ) : selectedNode.data.label === "Wait" ? (
                  <div>
                    <strong>Wait</strong>
                    <br />
                    <small>Days: {nodeDetails.days}</small>
                  </div>
                ) : (
                  <div>
                    <strong>Lead Source</strong>
                    <br />
                    <small>Email IDs: {nodeDetails.emails.split(",").length}</small>
                  </div>
                ),
            },
          }
        : node
    );

    setNodes(updatedNodes);

    // API call to backend for "Cold Email" or "Wait"
    if (selectedNode.data.label === "Cold Email") {
      const { subject, body, time } = nodeDetails;
      try {
        await axios.post("http://localhost:5000/api/send-emails", {
          subject,
          body,
          emailList: nodes
            .find((n) => n.data.label === "Lead Source")
            ?.data.details.emails.split(","),
          scheduledTime: new Date(`1970-01-01T${time}:00`).toISOString(),
          delay: parseInt(nodes.find((n) => n.data.label === "Wait")?.data.details.days || "0", 10),
        });
        alert("Emails scheduled successfully!");
      } catch (error) {
        console.error("Error scheduling emails:", error);
        alert("Failed to schedule emails.");
      }
    }

    setSelectedNode(null);
    setNodeDetails({ subject: "", body: "", time: "", days: "", emails: "" });
  };

  return (
    <div style={{ height: "100vh" }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={handleNodeClick}
        fitView
        style={{ pointerEvents: selectedNode ? "none" : "auto" }} 
      >
        <MiniMap />
        <Controls />
        <Background />
      </ReactFlow>
      <div style={{ position: "absolute", top: 10, right: 10 }}>
        <button onClick={() => addNode("Cold Email")}>Add Cold Email</button>
        <button onClick={() => addNode("Wait")}>Add Wait</button>
        <button onClick={() => addNode("Lead Source")}>Add Lead Source</button>
      </div>

      {selectedNode && (
        <div
          style={{
            position: "absolute",
            top: "20%",
            left: "20%",
            width: "300px",
            padding: "20px",
            backgroundColor: "white",
            border: "1px solid #ccc",
            borderRadius: "8px",
            zIndex: 10,
          }}
        >
          <h3>
            {selectedNode.data.label === "Cold Email"
              ? "Cold Email Details"
              : selectedNode.data.label === "Wait"
              ? "Wait Node Details"
              : "Lead Source Details"}
          </h3>
          {selectedNode.data.label === "Cold Email" && (
            <>
              <label>
                Subject:
                <input
                  type="text"
                  name="subject"
                  value={nodeDetails.subject}
                  onChange={handleDetailChange}
                  style={{ width: "100%", marginBottom: "10px" }}
                />
              </label>
              <label>
                Body:
                <textarea
                  name="body"
                  value={nodeDetails.body}
                  onChange={handleDetailChange}
                  style={{ width: "100%", marginBottom: "10px" }}
                />
              </label>
              <label>
                Time:
                <input
                  type="time"
                  name="time"
                  value={nodeDetails.time}
                  onChange={handleDetailChange}
                  style={{ width: "100%", marginBottom: "10px" }}
                />
              </label>
            </>
          )}
          {selectedNode.data.label === "Wait" && (
            <label>
              Days:
              <input
                type="number"
                name="days"
                value={nodeDetails.days}
                onChange={handleDetailChange}
                style={{ width: "100%", marginBottom: "10px" }}
              />
            </label>
          )}
          {selectedNode.data.label === "Lead Source" && (
            <label>
              Email IDs (comma-separated):
              <textarea
                name="emails"
                value={nodeDetails.emails}
                onChange={handleDetailChange}
                style={{ width: "100%", marginBottom: "10px" }}
              />
            </label>
          )}
          <button onClick={saveNodeDetails} style={{ marginRight: "10px" }}>
            Save
          </button>
          <button onClick={() => setSelectedNode(null)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default FlowChart;
